import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { signInFailure, signInPending, signInSuccess } from '../redux/userslice'

const Signin = () => {
    const [formData, setFormData] = useState({})
    const { loading, error  } = useSelector(state => state.user)
    const [postToStorage, setPostToStorage] = useState(false)
    const navigate = useNavigate()
    const dispatch = useDispatch()

    const handleChange = (e) => {
        setFormData({...formData, [e.target.id]: e.target.value})
    }


    useEffect( () => { 
    const handleUserFetch = async () => {
            try {
            dispatch(signInPending())
            if(localStorage.getItem(userData)){
                dispatch(signInSuccess(localStorage.getItem(JSON.parse(userData))))
                setPostToStorage(true)
            }else if (!postToStorage){
                const res = await fetch('http://localhost:5000/api/signup', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/Json'
                    },
                    body: JSON.stringify(formData)
                });
                if(data.success === false) {
                   dispatch(signInFailure(data.message))
                    return;
                }
                const userData = await res.json()
                localStorage.setItem('userData', JSON.stringify(userData))
                dispatch(signInSuccess(userData))
                navigate('/')
            }

        } catch(err) {
           dispatch(signInFailure(err.message))
        }
    }}, [postToStorage])


  return (
    <div>
      <h2>sign in</h2>
      <form>
        <input type="email" placeholder='email' id='email' onchange={handleChange}/>
        <input type="password" placeholder='password' id='password' onchange={handleChange}/>
        <button onSubmit={handleUserFetch} disabled={loading}>{loading ? 'loading...' : 'signin'}</button>
      </form>
      <div>
        <p>Dont have an account ?</p>
        <Link to='/signup'>
        <span>signup</span>
        </Link>
      </div>
      <div>{error && <p>{error}</p>}</div>
    </div>
  )
}

export default Signin
