import React from 'react'
import { useState } from 'react';


const handle = () => {
  const [name, setName] = useState('fola');

    const handleNameChange = () => {
        const names = ['precious', 'mercy', 'damilare'];
        const int = Math.floor(Math.random() * 3);
        
        setName(names[init])
    }
  
  return (
    <> hello {name} {handleNameChange()}s</>
  )
}

export default handle
