import { createSlice } from "@reduxjs/toolkit";


const initialState = {
    items: [],
    loading: false,
    error: null

}
const productsSlice = createSlice({
    name: 'products',
    initialState,
    reducers: {}
})


export const productsReducer = productsSlice.reducer;
