import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios"


/*const productsFetch = createAsyncThunk('products/productsFetch', async () => {
  const res = await axios.get("http://localhost:4000/test");
  console.log(res.data)
})*/

const fetch = async () => {

const res = await fetch('http://localhost:4000/test', {
    method: 'GET',
    headers: {
        'Content-Type': 'application/json' 
    },
    });
    const data = await res.json()
    console.log(data)
}

fetch()

const initialState = {
    items: [],
    loading: false,
    error: null

}
const productsSlice = createSlice({
    name: 'products',
    initialState,
    reducers: {},
    extraReducers: {
        [productsFetch.pending]: (state, action) => {
            state.loading = 'loading'
        },
        [productsFetch.success]: (state, action) => {
            state.loading = 'loading'
        },
        [productsFetch.failure]: (state, action) => {
            state.loading = 'loading'
        },
    }
})


export default productsSlice.reducer;
