import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

const Header = () => {
    const { cartTotalQuantity } = useSelector(state => state.cart);

    return (
        <div style={headerContainer}>
            <div style={headerTitle}>
                <h2>CareerKings</h2>
            </div>
            <div style={headerCart}>
                <Link to="/cart" style={cartLink}>
                    Cart <span style={cartQuantity}>{cartTotalQuantity}</span>
                </Link>
            </div>
        </div>
    );
};

const headerContainer = {
    backgroundColor: 'grey',
    display: 'flex',
    justifyContent: 'space-between',
    height: '80px',
    alignItems: 'center',
    padding: '0 30px'
};

const headerTitle = {};

const headerCart = {};

const cartLink = {
    color: 'white',
    textDecoration: 'none',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'center'
};

const cartQuantity = {
    marginLeft: '5px'
};

export default Header;
