import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { productsFetchFailure, productsFetchPending, productsFetchSuccess } from '../redux/productSlice';
import { addToCart } from '../redux/cartSlice';

const Home = () => {
    const  {cartItems } = useSelector(state => state.cart);
    const { products, loading, error } = useSelector((state) => state.product);
    const dispatch = useDispatch();


    useEffect(() => {
        const fetchData = async () => {
            try {
                dispatch(productsFetchPending);
                const res = await fetch('http://localhost:5000/products');
                const data = await res.json();
                if (!res.ok) {
                    throw new Error('Products cannot be fetched');
                }
                dispatch(productsFetchSuccess(data))
                console.log(data);
            } catch (error) {
                dispatch(productsFetchFailure(error.message));
            }
        };
        fetchData();
    }, []);

    const handleClick = () => {
        dispatch(addToCart())
       }

    return (
        <div>
            {error && <p>{error}</p>}
            {loading && <p>Loading...</p>}
            {products && 
                products.map(product => (
                    <div key={product.id} style={{display: 'grid', gridTemplateColumns: '1fr 1fr'}}>
                        <p>{product.name}</p>
                        <p>{product.price}</p>
                        <p>{product.desc}</p>
                        <img style={{height: '200px', width: '300px'}} src={product.image} alt={product.name} />
                        <button onClick={handleClick}>addToCart</button>
                    </div>
                ))}
        </div>
    );
};

export default Home;
