import {React, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { addToCart, removeFromCart, decreaseCart, clearCart, getTotals } from '../redux/cartSlice'

const Cart = () => {
    const { cartItems, cartTotalQuantity, cartTotalAmount } = useSelector(state => state.cart)
    const dispatch = useDispatch()


  const handleRemovefromcart = (cartItem) => {
    dispatch(removeFromCart(cartItem))

  }
  const handleIncreasecart = (cartItem) => {
    dispatch(addToCart(cartItem))


  }
  
  const handleDecreasecart = (cartItem) => {
    dispatch(decreaseCart(cartItem))


  }
  
  const handleClearcart = () => {
    dispatch(clearCart())


  }
 
  useEffect(() => { 
    const handleClick = () => { 
        dispatch(getTotals())
    }
    handleClick()
}, [dispatch])

  return (
    <div>
        <h2>products cart</h2>
     <div> 
     {Cart.length === 0 ? 
        <div>
         <p> your cart is empty</p>
         </div> : 
        <div>
             <div>
                <h2>PRODUCT</h2>
                <h2>PRICE</h2>
                <h2>QUANTITY</h2>
                <h2>TOTAL</h2>
            </div> 
            <div>
            {cartItems.map( cartItem => {
                <div>
                    <div>
                    <img src={cartItem.image} alt={cartItem.name} />
                    <h3>{cartItem.name}</h3>
                    <p>{cartItem.desc}</p>
                    <button onClick={() => handleRemovefromcart(cartItem)}>Remove</button>
                    </div>
                    <div>{cartItem.price}</div>
                    <div>
                        <span onClick={() => handleDecreasecart(cartItem)}>-</span>
                        <div>{cartItem.quantity}</div>
                        <span onClick={() => handleIncreasecart(cartItem)}>+</span>
                    </div>
                    <div>{cartItem.price * cartItem.quantity}</div>
                </div>
            })}
            </div>
            
           <button onClick={handleClearcart}>clear cart</button>
           <div>{cartTotalAmount}</div>
        </div>
         }
        </div>
    </div>
  )
}


export default Cart
