import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { productsFetchFailure, productsFetchPending, productsFetchSuccess } from '../redux/productSlice';
import { addToCart } from '../redux/cartSlice';

const Home = () => {
    const { products, loading, error } = useSelector(state => state.product);
    const dispatch = useDispatch();

    useEffect(() => {
        const fetchData = async () => {
            try {
                dispatch(productsFetchPending());
                const res = await fetch('http://localhost:5000/products');
                if (!res.ok) {
                    throw new Error('Products cannot be fetched');
                }
                const data = await res.json();
                dispatch(productsFetchSuccess(data));
            } catch (error) {
                dispatch(productsFetchFailure(error.message));
            }
        };
        fetchData();
    }, [dispatch]);

    const handleClick = (product) => {
        dispatch(addToCart(product));
    };

    return (
        <div style={container}>
            {error && <p>{error}</p>}
            {loading ? (
                <p>Loading...</p>
            ) : (
                <div>
                    {products && products.map(product => (
                        <div key={product.id} style={productContainer}>
                            <p>{product.name}</p>
                            <p>{product.price}</p>
                            <p>{product.desc}</p>
                            <img style={productImage} src={product.image} alt={product.name} />
                            <button onClick={() => handleClick(product)} style={addToCartButton}>Add to Cart</button>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

const container = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center'
};

const productContainer = {
    border: '1px solid #ccc',
    borderRadius: '5px',
    padding: '10px',
    margin: '10px',
    textAlign: 'center'
};

const productImage = {
    height: '200px',
    width: '300px'
};

const addToCartButton = {
    cursor: 'pointer',
    backgroundColor: 'blue',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    padding: '8px',
    marginTop: '10px'
};


